  const[text,setText]=useState()
  const[messages,setMessages]=useState([])
    function handleFormSubmit(event) {
      event.preventDefault()
      setMessages([...messages,{body:text, me:true}])
setTimeout(()=> fetch(`http://localhost:3001/message/${text}`).then
      (res=>res.json() ).then((data)=>setMessages([...messages,{body:text, me:true},{body:data.message , me:false}])),1000
)
    }


console.log(messages)

  return (
     <div className="App">
      <MessageList messages={messages}/>
      <MessageForm setText={setText} handleFormSubmit={handleFormSubmit} />
     </div>
  );
}

