import './App.css';

function App() {
  const[text,setText]=useState()
  const[person,setPerson]=useState('me')
  const[messages,setMessages]=useState([])
    function handleFormSubmit(event) {
      event.preventDefault()

      fetch(`http://localhost:3001/message/${text}`).then
      (res=>res.json() ).then((data)=>setMessages([...messages,{body:text}]))

setPerson('other')
    }

    useEffect(()=>{
      setPerson('me')
    },[text])

    console.log(text)
  return (
     <div className="App">
      <MessageList messages={messages}/>
      <MessageForm setText={setText} handleFormSubmit={handleFormSubmit} />
     </div>
  );
}

export default App;
